import React, { useEffect, useState } from 'react';
import { fetchData } from './utils/api';
import UnsettledBets from './components/UnsettledBets';
import AllBets from './components/AllBets';
import DateFilter from './components/DateFilter'; // Import the DateFilter component
import PivotTable from './components/PivotTable'; // Import the PivotTable component
import CumulativeGraph from './components/CumulativeGraph'; // Import the CumulativeGraph component
import './styles/App.css';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('all'); // 'all', 'unsettled', 'bySport', 'byDay', 'graph'
  const [searchTerm, setSearchTerm] = useState(''); // State for match search term
  const [sportTerm, setSportTerm] = useState(''); // State for sport search term
  const [dateFilter, setDateFilter] = useState({ startDate: null, endDate: null }); // State for date filter
  const [sortColumn, setSortColumn] = useState(null); // State for current sort column
  const [sortDirection, setSortDirection] = useState('asc'); // State for sort direction ('asc' or 'desc')

  useEffect(() => {
    const getData = async () => {
      const sheetData = await fetchData();
      setData(sheetData);
      setLoading(false);
    };
    getData();
  }, []);

  // Function to handle sorting
  const handleSort = (column) => {
    if (sortColumn === column) {
      // Toggle sort direction
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortDirection('asc'); // Default to ascending on new sort
    }
  };

  // Filtering the data based on search terms and date range
  const filteredData = data.filter((bet) => {
    const matchIncludesTerm = bet[4] && bet[4].toLowerCase().includes(searchTerm.toLowerCase());
    const sportIncludesTerm = bet[3] && bet[3].toLowerCase().includes(sportTerm.toLowerCase());

    // Date range filtering
    const betDate = new Date(bet[0]); // Assuming bet date is in the first column (index 0)
    const isWithinDateRange =
      (!dateFilter.startDate || betDate >= dateFilter.startDate) &&
      (!dateFilter.endDate || betDate <= dateFilter.endDate);

    return matchIncludesTerm && sportIncludesTerm && isWithinDateRange;
  });

  // Sorting the filtered data
  const sortedData = [...filteredData].sort((a, b) => {
    if (!sortColumn) return 0;
    const aValue = a[sortColumnIndexes[sortColumn]];
    const bValue = b[sortColumnIndexes[sortColumn]];
    if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
    if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
    return 0;
  });

  // Column index mappings for sorting (update these based on your data structure)
  const sortColumnIndexes = {
    date: 0,
    match: 4,
    sport: 3,
    odds: 6,
    result: 8,
    profit: 9, // Replace with the correct index for profit/loss column
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Welcome to the Betting Tips App!</h1>

      {/* Search bar for filtering bets by match */}
      <input
        type="text"
        placeholder="Search by Match..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Search bar for filtering bets by sport */}
      <input
        type="text"
        placeholder="Search by Sport..."
        value={sportTerm}
        onChange={(e) => setSportTerm(e.target.value)}
      />

      {/* Date Filter Component */}
      <DateFilter onFilterChange={(newFilter) => setDateFilter(newFilter)} />

      {/* Buttons to switch between different views */}
      <div className="view-toggle">
        <button onClick={() => setView('all')}>All Bets</button>
        <button onClick={() => setView('unsettled')}>Unsettled Bets</button>
        <button onClick={() => setView('bySport')}>Pivot by Sport</button>
        <button onClick={() => setView('byDay')}>Pivot by Day</button>
        <button onClick={() => setView('graph')}>Cumulative Graph</button> {/* Add Graph Button */}
      </div>

      {/* Sorting Controls */}
      <div className="sort-controls">
        <button onClick={() => handleSort('date')}>Sort by Date</button>
        <button onClick={() => handleSort('match')}>Sort by Match</button>
        <button onClick={() => handleSort('sport')}>Sort by Sport</button>
        <button onClick={() => handleSort('odds')}>Sort by Odds</button>
        <button onClick={() => handleSort('profit')}>Sort by Profit/Loss</button>
      </div>

      {/* Conditionally render components based on the selected view */}
      {view === 'all' && <AllBets data={sortedData} />}
      {view === 'unsettled' && <UnsettledBets data={sortedData} />}
      {view === 'bySport' && <PivotTable data={data} initialGroupBy="Sport" />}
      {view === 'byDay' && <PivotTable data={data} initialGroupBy="Day" />}
      {view === 'graph' && <CumulativeGraph data={data} />} {/* Render CumulativeGraph */}
    </div>
  );
}

export default App;
