import React from 'react';

function SummaryBySport({ data }) {
  // Remove header row by slicing data from index 1
  const filteredData = data.slice(1);

  // Calculate summary by sport
  const summary = filteredData.reduce((acc, bet) => {
    const sport = bet[3];
    const stake = parseFloat(bet[2]);
    const profitLoss = parseFloat(bet[10]);
    const unitProfitLoss = parseFloat(bet[9]);

    if (!acc[sport]) {
      acc[sport] = {
        sport,
        stake: 0,
        profitLoss: 0,
        unitProfitLoss: 0,
        numBets: 0,
      };
    }

    acc[sport].stake += stake;
    acc[sport].profitLoss += profitLoss;
    acc[sport].unitProfitLoss += unitProfitLoss;
    acc[sport].numBets += 1;

    return acc;
  }, {});

  const summaryArray = Object.values(summary);

  const totalStake = summaryArray.reduce((acc, row) => acc + row.stake, 0);
  const totalProfitLoss = summaryArray.reduce((acc, row) => acc + row.profitLoss, 0);
  const totalUnitProfitLoss = summaryArray.reduce((acc, row) => acc + row.unitProfitLoss, 0);
  const totalBets = summaryArray.reduce((acc, row) => acc + row.numBets, 0);

  return (
    <div className="summary-table-container">
      <h2>Summary by Sport</h2>
      <table className="summary-table">
        <thead>
          <tr>
            <th>Sport</th>
            <th>Stake</th>
            <th>P/L</th>
            <th>Unit P/L</th>
            <th>No. Bets</th>
            <th>ROI (%)</th>
          </tr>
        </thead>
        <tbody>
          {summaryArray.map((row, index) => (
            <tr key={index} className={row.profitLoss > 0 ? 'positive-row' : 'negative-row'}>
              <td>{row.sport}</td>
              <td>{row.stake.toFixed(2)}</td>
              <td>{row.profitLoss.toFixed(2)}</td>
              <td>{row.unitProfitLoss.toFixed(2)}</td>
              <td>{row.numBets}</td>
              <td>{((row.profitLoss / row.stake) * 100).toFixed(2)}%</td>
            </tr>
          ))}
          {/* Summary row */}
          <tr className={totalProfitLoss > 0 ? 'positive-row' : 'negative-row'}>
            <td>Total</td>
            <td>{totalStake.toFixed(2)}</td>
            <td>{totalProfitLoss.toFixed(2)}</td>
            <td>{totalUnitProfitLoss.toFixed(2)}</td>
            <td>{totalBets}</td>
            <td>{((totalProfitLoss / totalStake) * 100).toFixed(2)}%</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default SummaryBySport;
