import React from 'react';
import '../styles/App.css'; // Ensure the correct path to the App.css file

function SummaryBySport({ data }) {
  // Extract relevant data from the dataset
  const summaryData = data.reduce((acc, bet) => {
    const sport = bet[3]; // Assuming sport is in column index 3
    const stake = parseFloat(bet[2]) || 0; // Assuming stake is in column index 2
    const profit = parseFloat(bet[9]) || 0; // Assuming P/L is in column index 9
    const unitPL = parseFloat(bet[8]) || 0; // Assuming Unit P/L is in column index 8

    if (!acc[sport]) {
      acc[sport] = { stake: 0, profit: 0, unitPL: 0, count: 0 };
    }

    acc[sport].stake += stake;
    acc[sport].profit += profit;
    acc[sport].unitPL += unitPL;
    acc[sport].count += 1;

    return acc;
  }, {});

  // Convert summary data to an array and calculate ROI
  const summaryArray = Object.entries(summaryData).map(([sport, values]) => ({
    sport,
    ...values,
    roi: values.stake > 0 ? (values.profit / values.stake) * 100 : 0,
  }));

  // Calculate totals for all sports
  const totalStake = summaryArray.reduce((acc, row) => acc + row.stake, 0);
  const totalProfit = summaryArray.reduce((acc, row) => acc + row.profit, 0);
  const totalUnitPL = summaryArray.reduce((acc, row) => acc + row.unitPL, 0);
  const totalBets = summaryArray.reduce((acc, row) => acc + row.count, 0);
  const totalROI = totalStake > 0 ? (totalProfit / totalStake) * 100 : 0;

  return (
    <div className="summary">
      <h2>Summary by Sport</h2>
      <table className="summary-table">
        <thead>
          <tr>
            <th>Sport</th>
            <th>Stake</th>
            <th>P/L</th>
            <th>Unit P/L</th>
            <th>No. of Bets</th>
            <th>ROI (%)</th>
          </tr>
        </thead>
        <tbody>
          {summaryArray.map((row, index) => (
            <tr
              key={index}
              className={row.profit >= 0 ? 'positive-row' : 'negative-row'}
            >
              <td>{row.sport}</td>
              <td>{row.stake.toFixed(2)}</td>
              <td>{row.profit.toFixed(2)}</td>
              <td>{row.unitPL.toFixed(2)}</td>
              <td>{row.count}</td>
              <td>{row.roi.toFixed(2)}%</td>
            </tr>
          ))}
          {/* Summary row at the bottom */}
          <tr className={totalProfit >= 0 ? 'positive-row' : 'negative-row'}>
            <td>Total</td>
            <td>{totalStake.toFixed(2)}</td>
            <td>{totalProfit.toFixed(2)}</td>
            <td>{totalUnitPL.toFixed(2)}</td>
            <td>{totalBets}</td>
            <td>{totalROI.toFixed(2)}%</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default SummaryBySport;
