import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function CumulativeGraph({ data }) {
  // Extract relevant data from the columns
  const cumulativePLData = data.map((row) => parseFloat(row[11])); // Column L: Cumulative P/L
  const cumulativeStakeData = data.map((row) => parseFloat(row[12])); // Column M: Cumulative Stake
  const betDetails = data.map((row) => row[5]); // Column F: Bet details

  // Filter out invalid data points
  let previousStake = -1;
  const filteredData = cumulativePLData
    .map((value, index) => ({
      pl: value,
      stake: cumulativeStakeData[index],
      betDetail: betDetails[index],
    }))
    .filter((item) => {
      // Check if stake and P/L are valid numbers and stake is strictly increasing
      const isValid = !isNaN(item.pl) && !isNaN(item.stake) && item.stake > previousStake;
      if (isValid) previousStake = item.stake; // Update previousStake if the current one is valid
      return isValid;
    });

  // Prepare data for chart
  const chartData = {
    labels: filteredData.map((item) => item.stake), // X-axis labels as cumulative stake values
    datasets: [
      {
        label: 'Cumulative P/L',
        data: filteredData.map((item) => item.pl),
        fill: false,
        backgroundColor: 'rgb(75, 192, 192)',
        borderColor: 'rgba(75, 192, 192, 0.2)',
        borderWidth: 2, // Make line thicker
      },
    ],
  };

  // Chart options with custom tooltip and bolder y=0 gridline
  const options = {
    scales: {
      x: {
        type: 'linear',
        title: {
          display: true,
          text: 'Cumulative Stake',
        },
        min: 0, // Set the minimum x-axis value to 0
        suggestedMax: Math.max(...filteredData.map((item) => item.stake)) + 100, // Dynamically adjust max based on data
      },
      y: {
        title: {
          display: true,
          text: 'Cumulative P/L',
        },
        grid: {
          color: (context) => (context.tick.value === 0 ? '#000' : '#ccc'), // Bolder line at y=0
          lineWidth: (context) => (context.tick.value === 0 ? 2 : 1), // Thicker line at y=0
        },
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          // Custom tooltip content
          label: (tooltipItem) => {
            const item = filteredData[tooltipItem.dataIndex];
            return [`${item.betDetail}`, `Cumulative P/L: ${item.pl.toFixed(2)}`];
          },
        },
      },
    },
  };

  return (
    <div>
      <h2>Cumulative P/L vs Cumulative Stake</h2>
      <Line data={chartData} options={options} />
    </div>
  );
}

export default CumulativeGraph;
