import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Title, Tooltip, Legend);

function CumulativeGraph({ data }) {
  // Extract the necessary columns for cumulative P/L and cumulative stake
  const cumulativePL = [];
  const cumulativeStake = [];

  let totalPL = 0;
  let totalStake = 0;

  // Assuming column M is index 12 and column S is index 18
  data.forEach((bet) => {
    const pl = parseFloat(bet[12]) || 0; // Replace with correct index for P/L column
    const stake = parseFloat(bet[18]) || 0; // Replace with correct index for stake column

    totalPL += pl;
    totalStake += stake;

    cumulativePL.push(totalPL);
    cumulativeStake.push(totalStake);
  });

  // Data for the chart
  const chartData = {
    labels: cumulativeStake.map((value, index) => `Bet ${index + 1}`),
    datasets: [
      {
        label: 'Cumulative P/L',
        data: cumulativePL,
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: false,
        tension: 0.4,
      },
    ],
  };

  // Chart options
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Cumulative P/L vs. Cumulative Stake',
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Cumulative Stake',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Cumulative P/L',
        },
        ticks: {
          callback: (value) => `${value}£`, // Add £ symbol to y-axis labels
        },
      },
    },
  };

  return (
    <div className="cumulative-graph">
      <Line data={chartData} options={options} />
    </div>
  );
}

export default CumulativeGraph;
