import React from 'react';
import '../styles/Summary.css'; // Make sure to create this CSS file for styling

function SummaryByDay({ data }) {
  // Group the data by date and calculate the required values
  const summaryData = data.reduce((acc, bet) => {
    const date = bet[0]; // Assuming date is in the first column (index 0)
    const stake = parseFloat(bet[2]) || 0; // Column C: Stake
    const pl = parseFloat(bet[10]) || 0; // Column K: P/L
    const unitPL = parseFloat(bet[9]) || 0; // Column J: Unit P/L

    if (!acc[date]) {
      acc[date] = { date, stake: 0, pl: 0, unitPL: 0, count: 0 };
    }

    acc[date].stake += stake;
    acc[date].pl += pl;
    acc[date].unitPL += unitPL;
    acc[date].count += 1;

    return acc;
  }, {});

  // Convert the summary data into an array for rendering
  const summaryArray = Object.values(summaryData);

  // Calculate the overall summary
  const overallSummary = summaryArray.reduce(
    (acc, item) => {
      acc.stake += item.stake;
      acc.pl += item.pl;
      acc.unitPL += item.unitPL;
      acc.count += item.count;
      return acc;
    },
    { stake: 0, pl: 0, unitPL: 0, count: 0 }
  );

  return (
    <div className="summary">
      <h2>Summary by Day</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Stake</th>
            <th>P/L</th>
            <th>Unit P/L</th>
            <th>No. of Bets</th>
            <th>ROI (%)</th>
          </tr>
        </thead>
        <tbody>
          {summaryArray.map((item, index) => (
            <tr key={index} className={item.pl > 0 ? 'positive-row' : 'negative-row'}>
              <td>{item.date}</td>
              <td>{item.stake.toFixed(2)}</td>
              <td>{item.pl.toFixed(2)}</td>
              <td>{item.unitPL.toFixed(2)}</td>
              <td>{item.count}</td>
              <td>{((item.pl / item.stake) * 100).toFixed(2)}%</td>
            </tr>
          ))}
          <tr className={overallSummary.pl > 0 ? 'positive-row' : 'negative-row'}>
            <td><strong>Overall</strong></td>
            <td>{overallSummary.stake.toFixed(2)}</td>
            <td>{overallSummary.pl.toFixed(2)}</td>
            <td>{overallSummary.unitPL.toFixed(2)}</td>
            <td>{overallSummary.count}</td>
            <td>{((overallSummary.pl / overallSummary.stake) * 100).toFixed(2)}%</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default SummaryByDay;
