import React, { useEffect, useState } from 'react';
import { fetchData } from './utils/api';
import UnsettledBets from './components/UnsettledBets';
import AllBets from './components/AllBets';
import DateFilter from './components/DateFilter';
import CumulativeGraph from './components/CumulativeGraph';
import ProfitLossGraph from './components/ProfitLossGraph';
import SummaryBySport from './components/SummaryBySport';
import SummaryByDay from './components/SummaryByDay';
import './styles/App.css';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sportTerm, setSportTerm] = useState('');
  const [dateFilter, setDateFilter] = useState({ startDate: null, endDate: null });
  const [filteredSport, setFilteredSport] = useState(null); // State to track selected sport
  const [filteredDate, setFilteredDate] = useState(null); // State to track selected date

  useEffect(() => {
    const getData = async () => {
      const sheetData = await fetchData();
      setData(sheetData);
      setLoading(false);
    };
    getData();
  }, []);

  // Filtering the data based on search terms, date range, and selected sport or date
  const filteredData = data.filter((bet) => {
    const matchIncludesTerm = bet[4] && bet[4].toLowerCase().includes(searchTerm.toLowerCase());
    const sportIncludesTerm = bet[3] && bet[3].toLowerCase().includes(sportTerm.toLowerCase());

    // Date range filtering
    const betDate = new Date(bet[0]);
    const isWithinDateRange =
      (!dateFilter.startDate || betDate >= dateFilter.startDate) &&
      (!dateFilter.endDate || betDate <= dateFilter.endDate);

    // Sport and date filtering
    const isSportFiltered = filteredSport ? bet[3] === filteredSport : true;
    const isDateFiltered = filteredDate ? betDate.toLocaleDateString() === filteredDate : true;

    return matchIncludesTerm && sportIncludesTerm && isWithinDateRange && isSportFiltered && isDateFiltered;
  });

  // Handle sport filter change
  const handleSportFilter = (sport) => {
    setFilteredSport(sport); // Update the filtered sport state
    setFilteredDate(null); // Reset date filter
    setView('all'); // Switch to AllBets view when a sport is clicked
  };

  // Handle date filter change
  const handleDateFilter = (date) => {
    setFilteredDate(date); // Update the filtered date state
    setFilteredSport(null); // Reset sport filter
    setView('all'); // Switch to AllBets view when a date is clicked
  };

  // Reset all filters
  const resetFilters = () => {
    setFilteredSport(null);
    setFilteredDate(null);
    setView('all');
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Welcome to the Betting Tips App!</h1>

      {/* Search bar for filtering bets by match */}
      <input
        type="text"
        placeholder="Search by Match..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Search bar for filtering bets by sport */}
      <input
        type="text"
        placeholder="Search by Sport..."
        value={sportTerm}
        onChange={(e) => setSportTerm(e.target.value)}
      />

      {/* Date Filter Component */}
      <DateFilter onFilterChange={(newFilter) => setDateFilter(newFilter)} />

      {/* Buttons to switch between different views */}
      <div className="view-toggle">
        <button onClick={() => setView('all')}>All Bets</button>
        <button onClick={() => setView('unsettled')}>Unsettled Bets</button>
        <button onClick={() => setView('bySport')}>Summary by Sport</button>
        <button onClick={() => setView('byDay')}>Summary by Day</button>
        <button onClick={() => setView('graph')}>Cumulative Graph</button>
        <button onClick={() => setView('profitLossGraph')}>Profit & Loss Graph</button>
      </div>

      {/* Conditionally render components based on the selected view */}
      {view === 'all' && <AllBets data={filteredData} />}
      {view === 'unsettled' && <UnsettledBets data={filteredData} />}
      {view === 'bySport' && <SummaryBySport data={data} onSportClick={handleSportFilter} />}
      {view === 'byDay' && <SummaryByDay data={data} onFilterChange={handleDateFilter} />}
      {view === 'graph' && <CumulativeGraph data={data} />}
      {view === 'profitLossGraph' && <ProfitLossGraph data={data} />}

      {/* Reset Filter Button */}
      <button onClick={resetFilters} style={{ marginTop: '20px' }}>
        Reset Filters
      </button>
    </div>
  );
}

export default App;
