import React from 'react';
import TableHeader from './TableHeader';
import './AllBets.css'; // Ensure that this file exists or adapt the path accordingly

function AllBets({ data }) {
  return (
    <div className="all-bets">
      <h2>All Bets</h2>
      <table>
        <TableHeader /> {/* Proper use of TableHeader inside table */}
        <tbody>
          {data.slice(1).map((bet, index) => {
            // Assume the status of the bet is in the last column (update the index accordingly)
            const status = bet[bet.length - 1].toLowerCase();
            const rowClassName = status === 'win' ? 'win-row' : status === 'lose' ? 'lose-row' : '';

            return (
              <tr key={index} className={rowClassName}>
                {bet.map((cell, i) => (
                  <td key={i}>{cell}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default AllBets;
