import React from 'react';

function SummaryByDay({ data, onDayClick }) {
  const filteredData = data.slice(1);

  // Calculate summary by day
  const summary = filteredData.reduce((acc, bet) => {
    const date = bet[0]; // Column A: Date
    const stake = parseFloat(bet[2]); // Column C: Stake
    const profitLoss = parseFloat(bet[10]); // Column K: Profit/Loss

    // Check for valid numeric data before proceeding
    if (isNaN(stake) || isNaN(profitLoss)) {
      return acc; // Skip rows with invalid data
    }

    if (!acc[date]) {
      acc[date] = {
        date,
        stake: 0,
        profitLoss: 0,
        numBets: 0,
      };
    }

    acc[date].stake += stake;
    acc[date].profitLoss += profitLoss;
    acc[date].numBets += 1;

    return acc;
  }, {});

  const summaryArray = Object.values(summary);

  // Calculate totals
  const totalStake = summaryArray.reduce((acc, row) => acc + row.stake, 0);
  const totalProfitLoss = summaryArray.reduce((acc, row) => acc + row.profitLoss, 0);
  const totalBets = summaryArray.reduce((acc, row) => acc + row.numBets, 0);

  return (
    <div className="summary-table-container">
      <h2>Summary by Day</h2>
      <table className="summary-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Stake</th>
            <th>P/L</th>
            <th>No. Bets</th>
          </tr>
        </thead>
        <tbody>
          {summaryArray.map((row, index) => (
            <tr
              key={index}
              className={row.profitLoss > 0 ? 'positive-row' : 'negative-row'}
              onClick={() => onDayClick(row.date)} // Handle day click
              style={{ cursor: 'pointer' }}
            >
              <td>{row.date}</td>
              <td>{row.stake.toFixed(2)}</td>
              <td>{row.profitLoss.toFixed(2)}</td>
              <td>{row.numBets}</td>
            </tr>
          ))}
          {/* Summary row (fixed position) */}
          <tr className={totalProfitLoss > 0 ? 'positive-row' : 'negative-row'}>
            <td>Total</td>
            <td>{totalStake.toFixed(2)}</td>
            <td>{totalProfitLoss.toFixed(2)}</td>
            <td>{totalBets}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default SummaryByDay;
