import React, { useEffect, useState } from 'react';
import { fetchData } from './utils/api';
import UnsettledBets from './components/UnsettledBets';
import AllBets from './components/AllBets';
import DateFilter from './components/DateFilter';
import CumulativeGraph from './components/CumulativeGraph';
import ProfitLossGraph from './components/ProfitLossGraph';
import SummaryBySport from './components/SummaryBySport';
import SummaryByDay from './components/SummaryByDay';
import './styles/App.css';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [view, setView] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sportTerm, setSportTerm] = useState('');
  const [dateFilter, setDateFilter] = useState({ startDate: null, endDate: null });
  const [filteredData, setFilteredData] = useState([]);
  const [selectedSport, setSelectedSport] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    const getData = async () => {
      const sheetData = await fetchData();
      setData(sheetData);
      setLoading(false);
    };
    getData();
  }, []);

  useEffect(() => {
    // Log to debug data filtering process
    console.log('Full Data:', data);

    // Filtering the data based on search terms, date range, selected sport, and selected date
    const filtered = data.filter((bet, index) => {
      const matchIncludesTerm = bet[4] && bet[4].toLowerCase().includes(searchTerm.toLowerCase());
      const sportIncludesTerm = bet[3] && bet[3].toLowerCase().includes(sportTerm.toLowerCase());

      // Filter by selected sport if any
      const sportMatches = selectedSport ? bet[3] === selectedSport : true;

      // Filter by selected date if any
      const dateMatches = selectedDate ? bet[0] === selectedDate : true;

      // Date range filtering
      const betDate = new Date(bet[0]); // Assuming bet date is in the first column (index 0)
      const isWithinDateRange =
        (!dateFilter.startDate || betDate >= dateFilter.startDate) &&
        (!dateFilter.endDate || betDate <= dateFilter.endDate);

      const isValidRow = matchIncludesTerm && sportIncludesTerm && isWithinDateRange && sportMatches && dateMatches;

      // Log each row and why it's included or excluded
      console.log(`Processing Row ${index + 1}:`, bet, `Valid: ${isValidRow}`);

      return isValidRow;
    });

    console.log('Filtered Data:', filtered);
    setFilteredData(filtered);
  }, [data, searchTerm, sportTerm, dateFilter, selectedSport, selectedDate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Welcome to the Betting Tips App!</h1>

      {/* Search bar for filtering bets by match */}
      <input
        type="text"
        placeholder="Search by Match..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Search bar for filtering bets by sport */}
      <input
        type="text"
        placeholder="Search by Sport..."
        value={sportTerm}
        onChange={(e) => setSportTerm(e.target.value)}
      />

      {/* Date Filter Component */}
      <DateFilter onFilterChange={(newFilter) => setDateFilter(newFilter)} />

      {/* Buttons to switch between different views */}
      <div className="view-toggle">
        <button onClick={() => setView('all')}>All Bets</button>
        <button onClick={() => setView('unsettled')}>Unsettled Bets</button>
        <button onClick={() => { setView('bySport'); setSelectedSport(null); }}>Summary by Sport</button> {/* Reset sport filter */}
        <button onClick={() => { setView('byDay'); setSelectedDate(null); }}>Summary by Day</button> {/* Reset date filter */}
        <button onClick={() => setView('graph')}>Cumulative Graph</button>
        <button onClick={() => setView('profitLossGraph')}>Profit & Loss Graph</button>
      </div>

      {/* Conditionally render components based on the selected view */}
      {view === 'all' && <AllBets data={filteredData} />}
      {view === 'unsettled' && <UnsettledBets data={filteredData} />}
      {view === 'bySport' && (
        <SummaryBySport
          data={data}
          onSportClick={(sport) => {
            setSelectedSport(sport);
            setView('all'); // Switch to 'all' view and apply sport filter
          }}
        />
      )}
      {view === 'byDay' && (
        <SummaryByDay
          data={data}
          onDateClick={(date) => {
            setSelectedDate(date);
            setView('all'); // Switch to 'all' view and apply date filter
          }}
        />
      )}
      {view === 'graph' && <CumulativeGraph data={data} />}
      {view === 'profitLossGraph' && <ProfitLossGraph data={data} />}
    </div>
  );
}

export default App;
